float price() {
  float sum = 0.0;
  int path_count = 1000;
  for (int path_index = 0; path_index < path_count; path_index++) {
    float underlying = simulate(path_index);
    sum += param1 * param2 * max(param4 - underlying, 0.) * param3;
  }
  sum /= path_count;
